SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_juros_multa_titulo ( nr_titulo_p bigint, dt_referencia_p timestamp, ie_pagar_receber_p text, ie_juros_multa_p text) RETURNS bigint AS $body$
DECLARE


ie_juros_multa_venc_orig_w	parametro_contas_receber.ie_juros_multa_venc_orig%type;
ie_considera_juros_w		varchar(1);
vl_juros_w			double precision;
vl_multa_w			double precision;
cd_estabelecimento_w		smallint;


BEGIN

if (ie_pagar_receber_p = 'P') then
	
	
    select	max(cd_estabelecimento)
    into STRICT	cd_estabelecimento_w
    from	titulo_pagar
    where	nr_titulo	= nr_titulo_p;

    ie_considera_juros_w := obter_param_usuario(851, 59, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, cd_estabelecimento_w, ie_considera_juros_w);
    SELECT * FROM calcular_juro_multa_titulo(nr_titulo_p, null, dt_referencia_p, ie_considera_juros_w, 'N', vl_juros_w, vl_multa_w) INTO STRICT vl_juros_w, vl_multa_w;
elsif (ie_pagar_receber_p = 'R') then
    select	max(cd_estabelecimento)
    into STRICT	cd_estabelecimento_w
    from	titulo_receber
    where	nr_titulo	= nr_titulo_p;

    select	coalesce(max(ie_juros_multa_venc_orig),'N')
    into STRICT	ie_juros_multa_venc_orig_w
    from	parametro_contas_receber
    where	cd_estabelecimento	= cd_estabelecimento_w;

    ie_considera_juros_w := obter_param_usuario(801, 20, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, cd_estabelecimento_w, ie_considera_juros_w);
	
	
	SELECT * FROM calcular_juro_multa_titulo(null, nr_titulo_p, dt_referencia_p, ie_considera_juros_w, ie_juros_multa_venc_orig_w, vl_juros_w, vl_multa_w) INTO STRICT vl_juros_w, vl_multa_w;
end if;

if (ie_juros_multa_p = 'J') then
	return	coalesce(vl_juros_w,0);
elsif (ie_juros_multa_p = 'M') then
	return	coalesce(vl_multa_w,0);
elsif (ie_juros_multa_p = 'A') then
	return	coalesce(vl_juros_w,0) + coalesce(vl_multa_w,0);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
