SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_juros_multa_tit_dep_iden (nr_seq_deposito_p bigint, ie_opcao_p text, nr_titulo_p bigint) RETURNS bigint AS $body$
DECLARE


/*
ie_opcao_p
'J' Retorna juros
'M' Retorna Multa
*/
ie_juros_multa_titulo_w		varchar(1);
vl_juros_w					double precision;
vl_multa_w					double precision;
dt_limite_deposito_w		timestamp;
vl_retorno_w				double precision;
nr_seq_alter_venc_w			alteracao_vencimento.nr_sequencia%type;


BEGIN

ie_juros_multa_titulo_w := Obter_Param_Usuario(9054, 11, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, obter_estabelecimento_ativo, ie_juros_multa_titulo_w);

if ( coalesce(nr_seq_deposito_p,0) <> 0 ) then

	select	coalesce(max(a.dt_limite_deposito),clock_timestamp())
	into STRICT	dt_limite_deposito_w
	from	deposito_identificado a
	where	a.nr_sequencia = nr_seq_deposito_p;

	if (ie_opcao_p = 'J') then

		if ( coalesce(ie_juros_multa_titulo_w,'N') = 'N' ) then
		    /*Aqui faz exatamente o que trazia antes, quando era  tratado pela function na visao  to_number(obter_juros_multa_titulo(nr_titulo,obter_data_limite_dep_ident(nr_seq_deposito),'R','J'))*/

			vl_juros_w := (obter_juros_multa_titulo(nr_titulo_p,dt_limite_deposito_w,'R','J'))::numeric;

		elsif ( coalesce(ie_juros_multa_titulo_w,'N') = 'S' ) then
			/*Aqui busca o saldo de juros do título*/

			select	max(a.vl_saldo_juros)
			into STRICT	vl_juros_w
			from	titulo_receber a
			where	a.nr_titulo	= nr_titulo_p;

		elsif ( coalesce(ie_juros_multa_titulo_w,'N') = 'P' ) then /*buscar da ultima alteracao de vencimento OS 898570*/
			select	max(nr_sequencia)
			into STRICT	nr_seq_alter_venc_w
			from	alteracao_vencimento a
			where	a.nr_titulo = nr_titulo_p;

			if (nr_seq_alter_venc_w IS NOT NULL AND nr_seq_alter_venc_w::text <> '') then
				/*busca o valor de juros da alteracao de  vencimento do titulo*/

				select	coalesce(max(a.vl_juros),0)
				into STRICT	vl_juros_w
				from	alteracao_vencimento a
				where	a.nr_titulo	= nr_titulo_p
				and		a.nr_sequencia = nr_seq_alter_venc_w;

			else
				/*se não tiver alteracao de vencimento, calcula juros conforme data de limite informada no depoisto*/

				vl_juros_w := (obter_juros_multa_titulo(nr_titulo_p,dt_limite_deposito_w,'R','J'))::numeric;

			end if;

		end if;

		vl_retorno_w := coalesce(vl_juros_w,0);

	elsif (ie_opcao_p = 'M') then

		if ( coalesce(ie_juros_multa_titulo_w,'N') = 'N' ) then
		    /*Aqui faz exatamente o que trazia antes, quando era  tratado pela function na visao  to_number(obter_juros_multa_titulo(nr_titulo,obter_data_limite_dep_ident(nr_seq_deposito),'R','J'))*/

			vl_multa_w := (obter_juros_multa_titulo(nr_titulo_p,dt_limite_deposito_w,'R','M'))::numeric;

		elsif ( coalesce(ie_juros_multa_titulo_w,'N') = 'S' ) then
			/*Aqui busca o saldo de juros do título*/

			select	max(a.vl_saldo_multa)
			into STRICT	vl_multa_w
			from	titulo_receber a
			where	a.nr_titulo	= nr_titulo_p;

		elsif ( coalesce(ie_juros_multa_titulo_w,'N') = 'P' ) then	/*buscar da ultima alteracao de vencimento OS 898570*/
			select	max(nr_sequencia)
			into STRICT	nr_seq_alter_venc_w
			from	alteracao_vencimento a
			where	a.nr_titulo = nr_titulo_p;

			if (nr_seq_alter_venc_w IS NOT NULL AND nr_seq_alter_venc_w::text <> '') then
				/*busca o valor de juros da alteracao de  vencimento do titulo*/

				select	coalesce(max(a.vl_multa),0)
				into STRICT	vl_multa_w
				from	alteracao_vencimento a
				where	a.nr_titulo	= nr_titulo_p
				and		a.nr_sequencia = nr_seq_alter_venc_w;

			else
				/*se não tiver alteracao de vencimento, calcula juros conforme data de limite informada no depoisto*/

				vl_multa_w := (obter_juros_multa_titulo(nr_titulo_p,dt_limite_deposito_w,'R','M'))::numeric;

			end if;

		end if;

		vl_retorno_w := coalesce(vl_multa_w,0);

	end if;

end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
