CREATE OR REPLACE FUNCTION obter_juro_multa_cheque ( nr_seq_cheque_p bigint, dt_referencia_p timestamp, ie_pagar_receber_p text, ie_juros_multa_p text) RETURNS bigint AS $body$
DECLARE


vl_juros_w			double precision;
vl_multa_w			double precision;
vl_retorno_w			double precision;
dt_vencimento_atual_w		timestamp;
dt_devolucao_w			timestamp;


BEGIN

if (ie_pagar_receber_p = 'R') then
	select	coalesce(a.dt_vencimento_atual,a.dt_vencimento),
		a.dt_devolucao
	into STRICT	dt_vencimento_atual_w,
		dt_devolucao_w
	from	cheque_cr a
	where	nr_seq_cheque = nr_seq_cheque_p;

	SELECT * FROM calcular_juro_multa_cheque(nr_seq_cheque_p, dt_referencia_p, dt_vencimento_atual_w, vl_juros_w, vl_multa_w) INTO STRICT vl_juros_w, vl_multa_w;
	if (ie_juros_multa_p = 'J') then
		vl_retorno_w	:=	coalesce(vl_juros_w,0);
	elsif (ie_juros_multa_p = 'M') then
		vl_retorno_w	:=	coalesce(vl_multa_w,0);
	elsif (ie_juros_multa_p = 'A') then
		vl_retorno_w	:=	coalesce(vl_juros_w,0) + coalesce(vl_multa_w,0);
	end if;
end if;

return vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
