CREATE OR REPLACE FUNCTION obter_justificativa_mat_rep ( cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_justificativa_w	varchar(2000) := '';


BEGIN
if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then
	begin
	select	substr(max(ds_justificativa),1,2000)
	into STRICT	ds_justificativa_w
	from	rep_justificativa_material
	where	cd_material = cd_material_p
	and 	obter_se_justif_padrao_medic(cd_material, nr_sequencia,wheb_usuario_pck.get_cd_estabelecimento,'N','N') = 'S';

	end;
end if;
return	ds_justificativa_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
