CREATE OR REPLACE FUNCTION obter_justificativa_prescr ( nr_prescricao_atual_p bigint, nr_prescricao_anterior_p bigint, cd_material_p bigint, nr_seq_prescr_material_p bigint) RETURNS varchar AS $body$
DECLARE

/*
	ATENÇÃO
	ESTA FUNCTION FOI CRIADA PARA ATENDER UMA SITUAÇÃO MUITO ESPECÍFICA DO
	HOSPITAL RIM E SUA UTILIZAÇÃO NÃO É RECOMENDADA.
	MAIORES DETALHES PODEM SER ENCONTRADOS, VISUALIZANDO O DANO E HISTÓRICOS
	DA OS60122.

*/
ds_just_anterior_w		varchar(2000);
ds_just_resultado_w		varchar(2000);
ie_imp_farmacia_w		varchar(1);


BEGIN

/* Pega a justificativa atual do medicamento */

select	max(ds_justificativa)
into STRICT	ds_just_resultado_w
from	prescr_material
where	nr_prescricao 	= nr_prescricao_atual_p
and	nr_sequencia		= nr_seq_prescr_material_p;

if (nr_prescricao_anterior_p IS NOT NULL AND nr_prescricao_anterior_p::text <> '') then

	/* Verifica se foi impresso pela farmácia */

	begin
	select	CASE WHEN coalesce(dt_emissao_farmacia::text, '') = '' THEN  'N'  ELSE 'S' END
	into STRICT	ie_imp_farmacia_w
	from	prescr_medica
	where	nr_prescricao		= nr_prescricao_anterior_p;
	exception
		when others then
			ie_imp_farmacia_w := 'N';
	end;

	if (ie_imp_farmacia_w = 'S') then

		/* Pega a justificativa anterior do material */

		select	max(ds_justificativa)
		into STRICT	ds_just_anterior_w
		from	prescr_material
		where	nr_prescricao	= nr_prescricao_anterior_p
		and	cd_material	= cd_material_p;

		/* Faz as comparações */

		if (ds_just_anterior_w = ds_just_resultado_w) then
			ds_just_resultado_w := '';
		end if;

	end if;
end if;

return	ds_just_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
