SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_justifi_antib ( nr_atendimento_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE




ds_retorno_w                    varchar(2000)  := null;
cd_material_generico_w          bigint;
nr_seq_ficha_tecnica_w          bigint;


BEGIN

select	max(nr_seq_ficha_tecnica),
	max(cd_material_generico)
into STRICT	nr_seq_ficha_tecnica_w,
	cd_material_generico_w
from	material
where	cd_material = cd_material_p;

SELECT  MAX(a.ds_justificativa)
into STRICT	ds_retorno_w
FROM    prescr_material a,
	prescr_medica b
WHERE	a.nr_prescricao   = b.nr_prescricao
aND	b.nr_atendimento  = nr_atendimento_p
AND 	a.ie_suspenso     <> 'S'
and   	a.nr_dia_util 	= 1
and	b.dt_prescricao between clock_timestamp() - interval '20 days' and clock_timestamp()
and   	(coalesce(b.dt_liberacao_medico, b.dt_liberacao) IS NOT NULL AND (coalesce(b.dt_liberacao_medico, b.dt_liberacao))::text <> '')
and	a.cd_material     in (       SELECT  cd_material_generico_w
				
				
union

				SELECT  cd_material_p
				
				
union

				select  cd_material
				from    material
				where   cd_material_generico    = cd_material_generico_w
				
union

				select  cd_material
				from    material
				where   nr_seq_ficha_tecnica    = nr_seq_ficha_tecnica_w);




return substr(ds_retorno_w,1,2000);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_justifi_antib ( nr_atendimento_p bigint, cd_material_p bigint) FROM PUBLIC;

