CREATE OR REPLACE FUNCTION obter_justif_agenda_opme (nr_seq_agenda_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
nr_seq_consist_w	bigint;


BEGIN

if	(nr_seq_agenda_p > 0 AND cd_material_p   > 0) then

	/*Pegar sempre o maior, por que uma nova cota pode ser cadastrada*/

	select max(nr_sequencia)
	into STRICT nr_seq_consist_w
	from consistencia_agenda
	where nr_seq_agenda = nr_seq_agenda_p
	  and cd_material = cd_material_p;

	select ds_justificativa
	into STRICT ds_retorno_w
	from consistencia_agenda
	where nr_sequencia = nr_seq_consist_w;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
