CREATE OR REPLACE FUNCTION obter_justif_utiliz_disp_pepo ( nr_prescricao_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_justif_utilizacao_w	varchar(255);
nr_cirurgia_w		bigint;


BEGIN

select 	max(nr_cirurgia)
into STRICT	nr_cirurgia_w
from	cirurgia
where	nr_prescricao = nr_prescricao_p;

if (nr_cirurgia_w > 0) then
	select	substr(max(ds_justif_utilizacao),1,255)
	into STRICT	ds_justif_utilizacao_w
	from   	cirurgia_agente_disp
	where  	nr_cirurgia	= nr_cirurgia_w
	and	cd_material 	= cd_material_p;
end if;

return	ds_justif_utilizacao_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
