CREATE OR REPLACE FUNCTION obter_just_atraso_aprovacao ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_resultado			varchar(1);
qt_just_atraso			bigint;


BEGIN

qt_just_atraso := 0;

select 	count(*)
into STRICT	qt_just_atraso
from	proc_aprov_compra_just
where 	nr_seq_processo = nr_sequencia_p;

if (qt_just_atraso > 0) then
	ie_resultado :=	'S';
elsif (qt_just_atraso < 1) then
	ie_resultado :=	'N';
end if;


return	ie_resultado;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
