CREATE OR REPLACE FUNCTION obter_just_dose_ashworth (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255) := '';
ds_justificativa_w	varchar(4000);
nr_seq_motivo_w		bigint;
ds_motivo_w			varchar(100);

BEGIN

select	max(ds_justificativa),
		max(nr_seq_motivo)
into STRICT	ds_justificativa_w,
		nr_seq_motivo_w
from	ATEND_TOXINA_ITEM_HIST
where	nr_seq_item = nr_sequencia_p;


if (nr_seq_motivo_w IS NOT NULL AND nr_seq_motivo_w::text <> '') then
	select  max(ds_motivo)
	into STRICT	ds_motivo_w
	from 	MOTIVO_ALTER_DOSE_ESP
	where   nr_sequencia = nr_seq_motivo_w;

end if;

if (ds_motivo_w IS NOT NULL AND ds_motivo_w::text <> '') or (ds_justificativa_w IS NOT NULL AND ds_justificativa_w::text <> '') then

	ds_retorno_w := substr(ds_motivo_w || '  '||ds_justificativa_w,1,255);

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
