SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_just_interacao_item (nr_sequencia_p bigint, ds_justificativa_p text default null) RETURNS varchar AS $body$
DECLARE


ds_justificativa_w 	cpoe_material.ds_justificativa%type;

C01 CURSOR FOR
	SELECT 	distinct substr(b.ds_justificativa,1,2000) ds_justificativa
	from	alerta_api a,
			cpoe_justif_interacao b
	where 	a.nr_seq_cpoe = b.nr_seq_mat_cpoe
	and		a.cd_material = b.cd_material_interacao
	and		a.cd_api = b.cd_api
	and		coalesce(a.ds_sub_tipo_api, 'XPTO') = coalesce(b.ds_sub_tipo_api, 'XPTO')
	and		a.nr_gravidade = b.nr_gravidade
	and		a.ds_resultado_curto = b.ds_resultado_curto
	and		b.nr_seq_mat_cpoe = nr_sequencia_p
	and		coalesce(b.ie_integracao, 'XPTO') = 'LXC'
	
union all

	SELECT	distinct substr(b.ds_justificativa,1,2000) ds_justificativa
	from	cpoe_justif_interacao b
	where	((b.nr_seq_mat_cpoe = nr_sequencia_p) or (b.nr_seq_cpoe_princ = nr_sequencia_p))
	and		coalesce(b.ie_integracao, 'XPTO') <> 'LXC';

BEGIN

ds_justificativa_w := '';

for c01_w in C01
loop

	if (coalesce(position(c01_w.ds_justificativa in ds_justificativa_p),0) = 0) then
		ds_justificativa_w := substr(ds_justificativa_w || c01_w.ds_justificativa || chr(13),1,2000);
	end if;
end loop;

return ds_justificativa_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
