CREATE OR REPLACE FUNCTION obter_just_int_med_assoc_proc (nr_sequencia_p cpoe_material.nr_sequencia%type, cd_material_p cpoe_material.cd_material%type, ds_justificativa_p cpoe_material.ds_justificativa%type default null) RETURNS varchar AS $body$
DECLARE


ds_justificativa_w	cpoe_material.ds_justificativa%type;

C01 CURSOR FOR
	SELECT	distinct substr(a.ds_justificativa,1,2000) ds_justificativa
	from	cpoe_justif_interacao a
	where	((a.nr_seq_proc_cpoe = nr_sequencia_p) or (a.nr_seq_proc_princ = nr_sequencia_p))
	and		a.cd_material = cd_material_p
	and		coalesce(a.ie_integracao, 'XPTO') = 'MDX';

BEGIN

ds_justificativa_w := null;

for c01_w in C01
loop
	if (coalesce(position(c01_w.ds_justificativa in ds_justificativa_p),0) = 0) then
		ds_justificativa_w := substr(ds_justificativa_w || c01_w.ds_justificativa || chr(13),1,2000);
	end if;
end loop;

return ds_justificativa_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
