CREATE OR REPLACE FUNCTION obter_just_prescr_proc_autor (nr_prescricao_p bigint, nr_seq_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(4000);


BEGIN

select	max(ds_justificativa)
into STRICT	ds_retorno_w
from	prescr_procedimento
where	nr_prescricao	= nr_prescricao_p
and	nr_sequencia	= nr_seq_prescricao_p;

if (coalesce(ds_retorno_w::text, '') = '') then
	select	max(ds_justificativa)
	into STRICT	ds_retorno_w
	from	prescr_medica
	where	nr_prescricao	= nr_prescricao_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
