CREATE OR REPLACE FUNCTION obter_just_regra_prot_onc ( nr_seq_paciente_p bigint, nr_seq_material_p bigint, nr_seq_regra_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(255) := '';

BEGIN

if (nr_seq_paciente_p IS NOT NULL AND nr_seq_paciente_p::text <> '') and (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '') and (nr_seq_regra_p IS NOT NULL AND nr_seq_regra_p::text <> '') then

	Select 	substr(ds_justif_incons_quimio,1,255)
	into STRICT	ds_retorno_w
	from	paciente_atend_erro_just
	where	nr_seq_material = nr_seq_material_p
	and		nr_seq_paciente = nr_seq_paciente_p
	and		nr_seq_regra = nr_seq_regra_p;

end if;

return ds_retorno_w;

end;
$body$
