SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_kit_adep (nr_seq_cpoe_p bigint, nr_atendimento_p bigint, ie_tipo_item_p text, cd_item_p text) RETURNS varchar AS $body$
DECLARE


N_W constant varchar(1) := 'N';
S_W constant varchar(1) := 'S';
SOL_W constant varchar(3) := 'SOL';
MAT_W constant varchar(1) := 'M';

cd_kit_material_w varchar(2) := N_W;


BEGIN

begin
    if (ie_tipo_item_p IS NOT NULL AND ie_tipo_item_p::text <> '') and (nr_seq_cpoe_p IS NOT NULL AND nr_seq_cpoe_p::text <> '') and (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

                if (ie_tipo_item_p = SOL_W) then 
                    select	coalesce(max(S_W),N_W)
                    into STRICT cd_kit_material_w
                    from	kit_mat_prescricao
                    where	cd_material = (SELECT max(a.cd_material) from cpoe_material a
                                           where a.nr_sequencia = nr_seq_cpoe_p and a.nr_atendimento = nr_atendimento_p)
                    and	cd_estabelecimento	= obter_estabelecimento_ativo;
                elsif (ie_tipo_item_p = MAT_W and (cd_item_p IS NOT NULL AND cd_item_p::text <> '')) then 
                    select	coalesce(max(S_W),N_W)
                    into STRICT cd_kit_material_w
                    from	kit_mat_prescricao a
                    where	a.cd_material = cd_item_p
                    and	cd_estabelecimento	= obter_estabelecimento_ativo;
                end if;
                
    end if;
EXCEPTION
        when others then
        cd_kit_material_w := N_W;
end;

return	cd_kit_material_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
