CREATE OR REPLACE FUNCTION obter_kit_mat_prescr ( cd_material_p bigint, cd_estabelecimento_p bigint, ie_tipo_atendimento_p bigint, cd_setor_Atendimento_p bigint) RETURNS bigint AS $body$
DECLARE


C01 CURSOR FOR
	SELECT	cd_kit
	from	kit_mat_prescricao
	where	cd_material		= cd_material_p
	and	cd_estabelecimento	= cd_estabelecimento_p
	and	coalesce(ie_tipo_atendimento,ie_tipo_atendimento_p)	= ie_tipo_atendimento_p
	and	coalesce(cd_setor_Atendimento,cd_setor_Atendimento_p)= cd_setor_Atendimento_p
	and	obter_setor_excluir_kit(nr_sequencia,cd_setor_Atendimento_p) = 'N'
	order by 	coalesce(ie_tipo_atendimento,0),
			coalesce(cd_setor_Atendimento,0);

cd_kit_material_w	bigint;

BEGIN

open C01;
loop
fetch C01 into
	cd_kit_material_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

return	coalesce(cd_kit_material_w,obter_mat_estabelecimento(cd_estabelecimento_p,0,cd_material_p,'KT'));

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
