SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_label_atributo (nm_atrib_p text, nm_tabela_p text, nr_visao_p bigint) RETURNS varchar AS $body$
DECLARE


nm_label_w	varchar(60);
ds_retorno_w	varchar(60);

BEGIN
if (nm_atrib_p IS NOT NULL AND nm_atrib_p::text <> '') and (nr_visao_p IS NOT NULL AND nr_visao_p::text <> '') then

	select	replace(b.ds_label,'&','')
	into STRICT	nm_label_w
	from	tabela_visao_atributo b
	where	upper(b.nm_atributo)	= upper(nm_atrib_p)
	and	b.nr_sequencia		= nr_visao_p;

elsif (nm_atrib_p IS NOT NULL AND nm_atrib_p::text <> '') and (nm_tabela_p IS NOT NULL AND nm_tabela_p::text <> '') then
	select	replace(a.ds_label,'&','')
	into STRICT	nm_label_w
	from	tabela_atributo a
	where	upper(a.nm_atributo)	= upper(nm_atrib_p)
	and	upper(a.nm_tabela)	= upper(nm_tabela_p);

end if;

ds_retorno_w := nm_label_w;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
