CREATE OR REPLACE FUNCTION obter_label_class_diag (ds_atributo_p text) RETURNS varchar AS $body$
DECLARE


ds_label_w			varchar(100);
ds_atrib_dominio_w		valor_dominio.vl_dominio%type;


BEGIN

	if (ds_atributo_p = 'IE_DIAG_PRINC_EPISODIO') then
	    ds_atrib_dominio_w := 'DPE';
	end if;

	select  max(ds_curta)
	into STRICT	ds_label_w
	from    classificacao_diagnostico
	where   ie_classificacao_diagnostico = ds_atrib_dominio_w;

return    ds_label_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
