CREATE OR REPLACE FUNCTION obter_label_longo_atributo (nm_atrib_p text, nm_tabela_p text) RETURNS varchar AS $body$
DECLARE


nm_label_w    varchar(4000);
ds_retorno_w    varchar(4000);

BEGIN
if (nm_atrib_p IS NOT NULL AND nm_atrib_p::text <> '') and (nm_tabela_p IS NOT NULL AND nm_tabela_p::text <> '') then
    select  replace(a.ds_label_longo,'&','')
    into STRICT    nm_label_w
    from    tabela_atributo a
    where   upper(a.nm_atributo)    = upper(nm_atrib_p)
    and     upper(a.nm_tabela)    = upper(nm_tabela_p);

end if;

ds_retorno_w := nm_label_w;

return    ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
