CREATE OR REPLACE FUNCTION obter_label_mecanismos_calculo ( nm_tabela_p text, nm_atributo_p text) RETURNS varchar AS $body$
DECLARE



ds_retorno_w   calculos_engine_dest_cli.ds_label%type;

BEGIN
ds_retorno_w := null;
if (nm_tabela_p IS NOT NULL AND nm_tabela_p::text <> '') and (nm_atributo_p IS NOT NULL AND nm_atributo_p::text <> '') then
	select 	max(ds_label)
	into STRICT	   ds_retorno_w
	from	   calculos_engine_dest_cli
	where	   ds_inf_destino    = nm_tabela_p
   and      ds_campo_destino  = nm_atributo_p;
end if;

return	ds_retorno_w;

end;
$body$
