SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_lab_dados_lote ( nr_seq_lote_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/*ie_opcao_p		'L' Número seq Lote
			'GI' Grupo de Impressão
			'DI' Data Inicial
			'DF' Data final
			'DL' Descrição do Lote
*/
nr_seq_lote_w		bigint;
nr_seq_grupo_imp_w	bigint;
dt_inicial_w		timestamp;
dt_final_w			timestamp;
ds_lote_w			varchar(40);
ds_retorno_w		varchar(500);

BEGIN

select 	max(nr_sequencia),
	max(nr_seq_grupo_imp),
	max(dt_inicial),
	max(dt_final),
	max(ds_lote)
into STRICT	nr_seq_lote_w,
	nr_seq_grupo_imp_w,
	dt_inicial_w,
	dt_final_w,
	ds_lote_w
from	lab_lote_exame
where	nr_sequencia	= nr_seq_lote_p;

if (ie_opcao_p	= 'L') then
	ds_retorno_w := nr_seq_lote_w;
elsif (ie_opcao_p = 'GI') then
	ds_retorno_w := nr_seq_grupo_imp_w;
elsif (ie_opcao_p = 'DI') then
	ds_retorno_w := dt_inicial_w;
elsif (ie_opcao_p = 'DF') then
	ds_retorno_w := dt_final_w;
elsif (ie_opcao_p = 'DF') then
	ds_retorno_w := ds_lote_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
