CREATE OR REPLACE FUNCTION obter_lado_diag_3m ( ie_lado_p text) RETURNS varchar AS $body$
DECLARE


    nr_seq_regra_conv_w bigint;
    vl_lado_w           varchar(40);

BEGIN
    -- obter a regra
    SELECT max( b.nr_seq_regra_conv )
    INTO STRICT nr_seq_regra_conv_w
    FROM
        intpd_eventos a,
        intpd_eventos_sistema b
    WHERE a.ie_evento = 64
        AND a.nr_sequencia = b.nr_seq_evento;

    -- tratar o lado
    vl_lado_w := intpd_conv('DIAGNOSTICO_DOENCA','IE_LADO',ie_lado_p, nr_seq_regra_conv_w,'I', 'E');

    RETURN vl_lado_w;

END;
