CREATE OR REPLACE FUNCTION obter_lado_procedimento ( nr_prescricao_p bigint, nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE



ds_retorno_w		varchar(255) := '';
ie_lado_w	     	varchar(03);


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	select	max(a.ie_lado)
	into STRICT	ie_lado_w
	from	prescr_procedimento a		
	where	1 = 1
	and	a.nr_prescricao	= nr_prescricao_p
  and a.nr_sequencia = nr_sequencia_p;

  if (ie_lado_w IS NOT NULL AND ie_lado_w::text <> '') then
  begin
	if (ie_opcao_p	= 'C')	then
		ds_retorno_w	:= ie_lado_w;
	elsif (ie_opcao_p	= 'D')	then
		ds_retorno_w	:= obter_valor_dominio(1372, ie_lado_w);
	end if;
  end;
  else
    ds_retorno_w := ' ';
  end if;
end if;

RETURN	ds_retorno_w;

END	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
