CREATE OR REPLACE FUNCTION obter_lado_proced_princ_atend ( nr_atendimento_p bigint, ie_opcao_p text ) RETURNS varchar AS $body$
DECLARE


ie_tipo_atendimento_w		bigint;
cd_procedimento_w		bigint;
ie_origem_proced_w		bigint;
ie_lado_w			varchar(1);


BEGIN

select	ie_tipo_atendimento
into STRICT	ie_tipo_atendimento_w
from	atendimento_paciente
where	nr_atendimento		= 	nr_atendimento_p;

SELECT * FROM obter_proc_princ_interno(nr_atendimento_p, ie_tipo_atendimento_w, cd_procedimento_w, ie_origem_proced_w, 0) INTO STRICT cd_procedimento_w, ie_origem_proced_w;

select	max(ie_lado)
into STRICT	ie_lado_w
from	prescr_procedimento a,
	prescr_medica b
where	a.nr_prescricao		=	b.nr_prescricao
and	a.cd_procedimento	=	cd_procedimento_w
and	a.ie_origem_proced	=	ie_origem_proced_w
and	b.nr_atendimento	=	nr_atendimento_p;

if (ie_opcao_p = 'D') and (ie_lado_w IS NOT NULL AND ie_lado_w::text <> '') then
	return obter_valor_dominio(1372, ie_lado_w);
else
	return ie_lado_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
