SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_lado_proc_3m ( nr_seq_proc_paciente_p bigint) RETURNS varchar AS $body$
DECLARE


    nr_seq_regra_conv_w bigint;
    vl_lado_w           varchar(40);
    ie_lado_w           varchar(40);


BEGIN
    -- obter a regra
	SELECT 	DISTINCT( b.nr_seq_regra_conv )
	INTO STRICT 	nr_seq_regra_conv_w
	FROM 	intpd_eventos a,
		intpd_eventos_sistema b
	WHERE 	a.ie_evento = 64
	AND 	a.nr_sequencia = b.nr_seq_evento
	and 	(b.nr_seq_regra_conv IS NOT NULL AND b.nr_seq_regra_conv::text <> '');

    -- obter o lado
    SELECT DISTINCT(ie_lado)
    INTO STRICT ie_lado_w
    FROM procedimento_paciente
    WHERE nr_sequencia = nr_seq_proc_paciente_p;

    -- tratar o lado
    vl_lado_w := intpd_conv('PROCEDIMENTO_PACIENTE','IE_LADO',ie_lado_w, nr_seq_regra_conv_w,'I', 'E');

    RETURN vl_lado_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
