CREATE OR REPLACE FUNCTION obter_laudo_data_ditado ( nr_seq_laudo_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_ditado_w	timestamp;
nr_seq_prescr_proc_w	bigint;


BEGIN

select	max(c.nr_seq_interno)
into STRICT	nr_seq_prescr_proc_w
from	prescr_procedimento c,
	procedimento_paciente b,
	laudo_paciente a
where	b.nr_prescricao = c.nr_prescricao
and	b.nr_sequencia_prescricao = c.nr_sequencia
and	a.nr_seq_proc	= b.nr_sequencia
and	a.nr_sequencia =  nr_seq_laudo_p;


if (coalesce(nr_seq_prescr_proc_w::text, '') = '')  then

	select  max(b.nr_seq_interno)
	into STRICT	nr_seq_prescr_proc_w
	from 	prescr_procedimento b,
		laudo_paciente a
	where	a.nr_prescricao = b.nr_prescricao
	and	a.nr_seq_prescricao = b.nr_sequencia
	and	a.nr_sequencia =  nr_seq_laudo_p;
end if;

select	max(dt_atualizacao)
into STRICT	dt_ditado_w
from	prescr_proc_ditado
where	nr_seq_prescr_proc = nr_seq_prescr_proc_w;

return dt_ditado_w;

end;
$body$
