SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_laudo_padrao_montagem ( nr_seq_laudo_p bigint, nr_seq_proc_interno_p bigint ) RETURNS bigint AS $body$
DECLARE


qt_laudo_compl_w		bigint;
nr_seq_laudo_padrao_w		bigint := null;
nr_seq_laudo_padrao_tmp_w	bigint;
nr_seq_exame_compl_w		bigint;
ie_existe_w			varchar(1);

c01 CURSOR FOR
	SELECT	nr_sequencia
	from	laudo_pac_pato_padrao
	where	nr_seq_proc_interno	= nr_seq_proc_interno_p
	order 	by 1;


c02 CURSOR FOR
	SELECT	p.nr_seq_pato_exame
	from	laudo_pac_exame_compl l,
		prescr_proc_exame_compl p
	where	l.nr_seq_laudo		= nr_seq_laudo_p
	and	l.nr_seq_exame_compl 	= p.nr_sequencia
	order 	by 1;


BEGIN


select	count(*)
into STRICT	qt_laudo_compl_w
from	laudo_pac_exame_compl
where	nr_seq_laudo = nr_seq_laudo_p;


if	not(qt_laudo_compl_w > 0) then

	select 	max(a.nr_sequencia)
	into STRICT	nr_seq_laudo_padrao_w
	from 	laudo_pac_pato_padrao a
	where 	a.nr_seq_proc_interno = nr_seq_proc_interno_p
	and	not exists (SELECT 1 from laudo_pac_pato_exame_comp b where b.nr_seq_laudo_padrao = a.nr_sequencia);

else

	-- Busca entre os laudos padrões cadastrados, os que possuem o mesmo proc interno
	open c01;
	loop
	fetch c01 into
		nr_seq_laudo_padrao_tmp_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		ie_existe_w := 'N';

		-- Para cada laudo padrão, é verificado se todos os exames complementares do laudo são existentes no laudo padrão
		open c02;
		loop
		fetch c02 into
			nr_seq_exame_compl_w;
		EXIT WHEN NOT FOUND; /* apply on c02 */
			begin


			select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
			into STRICT	ie_existe_w
			from	laudo_pac_pato_exame_comp
			where	nr_seq_laudo_padrao 	= nr_seq_laudo_padrao_tmp_w
			and	nr_seq_exame_compl	= nr_seq_exame_compl_w;

			--Caso não exista algum dos exames complementares do laudo, não é utilizado o laudo padrão
			if (ie_existe_w = 'N') then
				exit;
			end if;

			end;
		end loop;
		close c02;

		-- caso o flag seja 'S', o laudo padrão possui todos os exames complementares que o laudo
		if (ie_existe_w = 'S') then
			exit;
		end if;


		end;
	end loop;
	close c01;

	if (ie_existe_w = 'S') then

		nr_seq_laudo_padrao_w := nr_seq_laudo_padrao_tmp_w;

	else	--caso não existe um laudo padrão para os exames complementares conforme laudo, é utilizado o laudo padrão do procedimento
		select 	max(a.nr_sequencia)
		into STRICT	nr_seq_laudo_padrao_w
		from 	laudo_pac_pato_padrao a
		where 	a.nr_seq_proc_interno = nr_seq_proc_interno_p
		and	not exists (SELECT 1 from laudo_pac_pato_exame_comp b where b.nr_seq_laudo_padrao = a.nr_sequencia);

	end if;

end if;

return	nr_seq_laudo_padrao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_laudo_padrao_montagem ( nr_seq_laudo_p bigint, nr_seq_proc_interno_p bigint ) FROM PUBLIC;

