CREATE OR REPLACE FUNCTION obter_legendas_presc_gpt ( ie_tipo_gpt_p text, nr_atendimento_p bigint, cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type, dt_inicial_p timestamp, dt_final_p timestamp, ie_reconciliacao_p text, ie_param_91_p text, ie_forma_reconciliacao_s_p text, ie_param_92_p text, cd_classif_setor_p bigint, dt_alta_medico_p timestamp, dt_alta_p timestamp, ie_lib_farm_p text, dt_inicio_analise_farm_p gpt_hist_analise_plano.dt_inicio_analise%type, ie_tipo_legenda_p text) RETURNS bigint AS $body$
DECLARE


nr_seq_cor_w			bigint;
ie_emergencia_w			varchar(1) := 'N';
ie_prioridade_revisao_w		varchar(1) := 'N';


BEGIN

select	CASE WHEN count(nr_prescricao)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_emergencia_w
from	prescr_medica
where	nr_atendimento = nr_atendimento_p
and	((ie_tipo_gpt_p = 'F' and (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')		
		and ( ie_lib_farm_p = 'N' or (coalesce(ie_lib_farm,'N') = 'S'))
		and	coalesce(dt_liberacao_farmacia::text, '') = '')
	or (ie_tipo_gpt_p = 'F' and coalesce(dt_liberacao::text, '') = ''))
and	((coalesce(ie_prescr_emergencia,'N') = 'S') or (coalesce(ie_emergencia,'N') = 'S'))
and	dt_prescricao between dt_inicial_p and dt_final_p;

	if (ie_tipo_gpt_p = 'F') then
		begin

			select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
			into STRICT 	ie_prioridade_revisao_w
			from 	prescr_medica a left join prescr_material b ON (a.nr_prescricao = b.nr_prescricao)
			where	a.nr_atendimento = nr_atendimento_p
			and 	a.dt_prescricao between dt_inicial_p and dt_final_p
			and (b.ie_urgencia = 'S' or (b.qt_dose_especial IS NOT NULL AND b.qt_dose_especial::text <> ''));


			if (ie_tipo_legenda_p = 'E' and ie_emergencia_w = 'S') then
				nr_seq_cor_w := 7398;

			elsif (ie_tipo_legenda_p = 'R' and ie_prioridade_revisao_w = 'S') or
				((ie_param_92_p = 'S') and ((cd_classif_setor_p IS NOT NULL AND cd_classif_setor_p::text <> '') and cd_classif_setor_p = 1)) then
				nr_seq_cor_w := 7397;

			elsif (ie_tipo_legenda_p = 'R1' and (ie_reconciliacao_p = 'S' and ie_param_91_p = '1') or (ie_forma_reconciliacao_s_p = 'R' and ie_param_91_p = '2'))then
				nr_seq_cor_w := 7400;

			elsif (ie_tipo_legenda_p = 'R2' and ie_forma_reconciliacao_s_p = 'A' and ie_param_91_p = '2') then
				nr_seq_cor_w := 7401;
				
			end if;
		end;
	 elsif (ie_tipo_gpt_p = 'E') then
		begin
			if (dt_alta_medico_p IS NOT NULL AND dt_alta_medico_p::text <> '') then
				nr_seq_cor_w := 7402;
				
			elsif (dt_alta_p IS NOT NULL AND dt_alta_p::text <> '') then
				nr_seq_cor_w := 7403;
				
			elsif (ie_emergencia_w = 'S') then
				nr_seq_cor_w := 7404;
			end if;
		end;
	end if;

return	nr_seq_cor_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
