CREATE OR REPLACE FUNCTION obter_legenda_acao_exec_teste ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*	PE - Pendente
	C - Conforme
	P - Parcialmente conforme
	N - NÃ£o conforme	*/
ds_retorno_w		varchar(1) := 'N';


BEGIN

if (coalesce(nr_sequencia_p,0) <> 0) then
	if (ie_opcao_p = 'PE') then
		begin
		select	'N'
		into STRICT	ds_retorno_w
		from	teste_soft_exec_acao_res
                where	nr_seq_acao_exec = nr_sequencia_p  LIMIT 1;
		exception
		when others then
			ds_retorno_w := 'S';
		end;
	elsif (ie_opcao_p = 'C') then
		begin
		select	'S'
		into STRICT	ds_retorno_w
		from	teste_soft_exec_acao_res
                where	nr_seq_acao_exec = nr_sequencia_p
		and	ie_resultado = 'C'  LIMIT 1;
		exception
		when others then
			ds_retorno_w := 'N';
		end;
	elsif (ie_opcao_p = 'P') then
		begin
		select	'S'
		into STRICT	ds_retorno_w
		from	teste_soft_exec_acao_res
                where	nr_seq_acao_exec = nr_sequencia_p
		and	ie_resultado = 'P'  LIMIT 1;
		exception
		when others then
			ds_retorno_w := 'N';
		end;
	elsif (ie_opcao_p = 'N') then
		begin
		select	'S'
		into STRICT	ds_retorno_w
		from	teste_soft_exec_acao_res
                where	nr_seq_acao_exec = nr_sequencia_p
		and	ie_resultado = 'N'  LIMIT 1;
		exception
		when others then
			ds_retorno_w := 'N';
		end;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
