CREATE OR REPLACE FUNCTION obter_legenda_cor (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

			 
ds_retorno_w		varchar(15);
ds_cor_w		varchar(15);
qt_dias_final_w		integer;
qt_dias_entre_w		bigint;

C01 CURSOR FOR 
	SELECT	ds_cor,  
		coalesce(qt_dias_final ,0) 
	from	leg_tempo_aguardando_vaga 
	where	qt_dias_entre_w >= qt_dias_inicial;


BEGIN 
 
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then 
 
	select	obter_dias_entre_datas(a.dt_solicitacao,clock_timestamp()) 
	into STRICT	qt_dias_entre_w 
	from	GESTAO_VAGA a 
	where	a.nr_sequencia = nr_sequencia_p;
	 
	open C01;
	loop 
	fetch C01 into	 
		ds_cor_w,	 
		qt_dias_final_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		if (qt_dias_final_w <> 0) then 
			if (qt_dias_final_w >= qt_dias_entre_w) then 
				ds_retorno_w := ds_cor_w;
			end if;
		else 
			ds_retorno_w := ds_cor_w;
		end if;
		end;
	end loop;
	close C01;
 
end if;
	 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
