CREATE OR REPLACE FUNCTION obter_legenda_evento (nr_seq_regra_tempo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_legenda_evento_w	varchar(255);


BEGIN

if (nr_seq_regra_tempo_p IS NOT NULL AND nr_seq_regra_tempo_p::text <> '') then
	select 	substr(ds_regra,1,255)
	into STRICT	ds_legenda_evento_w
	from 	pls_regra_tempo_event_ocor
	where 	nr_sequencia = nr_seq_regra_tempo_p
	and	ie_situacao = 'A';
end if;

return	ds_legenda_evento_w;

end;
$body$
LANGUAGE PLPGSQL
