SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_legenda_exames_js (nr_seq_exame_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_legenda_w	bigint;
ie_solicitacao_w	varchar(1);
ie_situacao_w		varchar(1);


BEGIN



select  ie_solicitacao,
	coalesce(ie_situacao,'A')
into STRICT	ie_solicitacao_w,
	ie_situacao_w
from    exame_laboratorio
where   nr_seq_exame   = nr_seq_exame_p;

if (ie_solicitacao_w = 'S') then
	nr_seq_legenda_w := 275;
else
	nr_seq_legenda_w := 276;
end if;

if (ie_situacao_w = 'I') then
	nr_seq_legenda_w := 277;
end if;

return	nr_seq_legenda_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
