CREATE OR REPLACE FUNCTION obter_legenda_idade_gpt (cd_pessoa_fisica_p bigint) RETURNS bigint AS $body$
DECLARE


qt_anos_w 	bigint;
nr_seq_cor_w	bigint;


BEGIN

qt_anos_w  := Obter_Idade_PF(cd_pessoa_fisica_p, clock_timestamp(), 'A');

select 		MAX(nr_sequencia)
into STRICT 		nr_seq_cor_w
from 		regra_legenda_classif
where		qt_anos_w between coalesce(QT_IDADE_MIN,0) and coalesce(QT_IDADE_MAX,999)
and		coalesce(ie_situacao,'I') = 'A';

return	nr_seq_cor_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
