CREATE OR REPLACE FUNCTION obter_legenda_idade (cd_pessoa_fisica_p bigint) RETURNS varchar AS $body$
DECLARE


qt_anos_w 	bigint;
ds_cor_w	varchar(10) := '';


BEGIN

qt_anos_w  := Obter_Idade_PF(cd_pessoa_fisica_p, clock_timestamp(), 'A');

select 		coalesce(MAX(ds_cor),'')
into STRICT 		ds_cor_w
from 		REGRA_LEGENDA_CLASSIF
where		qt_anos_w between coalesce(QT_IDADE_MIN,0) and coalesce(QT_IDADE_MAX,999)
and		coalesce(ie_situacao,'I') = 'A';

return	ds_cor_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
