CREATE OR REPLACE FUNCTION obter_legenda_isolamento (cd_tipo_isolamento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_cor_w	varchar(100);


BEGIN

SELECT 	ds_cor_ocupacao
into STRICT	ds_cor_w
FROM 	motivo_isolamento
where 	nr_sequencia = cd_tipo_isolamento_p;

