SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_legenda_laudo_reg ( nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


qt_retorno_w 		bigint;
cd_setor_registro_w	integer;
ds_setor_registro_w	varchar(10);
ie_exibe_legenda_w	varchar(1);
qt_result_w		bigint;


BEGIN


ds_setor_registro_w := coalesce(Obter_Valor_Param_Usuario(994,11, Obter_perfil_Ativo, wheb_usuario_pck.get_nm_usuario, 0),'0');
ie_exibe_legenda_w := coalesce(Obter_Valor_Param_Usuario(994,49, Obter_perfil_Ativo, wheb_usuario_pck.get_nm_usuario, 0),'N');

qt_retorno_w := 0;
qt_result_w := 1;


begin
cd_setor_registro_w := (ds_setor_registro_w)::numeric;

exception
when others then
cd_setor_registro_w := 0;
end;




if (cd_setor_registro_w <> 0) 	and (ie_exibe_legenda_w <> 'N')	then

	select	count(*)
	into STRICT	qt_retorno_w
	from   	laudo_paciente a
	where  	a.nr_sequencia = nr_sequencia_p
	and	not exists (	SELECT 	1
				from	laudo_paciente_loc d
				where  	d.cd_setor_registro = cd_setor_registro_w
				and	d.nr_sequencia = a.nr_sequencia);

	if (qt_retorno_w = 0) then

		qt_retorno_w := 0;

		select 	count(*)
		into STRICT	qt_retorno_w
		from   	laudo_paciente a
		where  	a.nr_sequencia = nr_sequencia_p
		and	exists (SELECT	1
				from	laudo_paciente_loc d
				where  	d.cd_setor_registro = cd_setor_registro_w
				and	d.nr_sequencia = a.nr_sequencia
				and	coalesce(d.dt_retirada::text, '') = '');

		qt_result_w := 2;

		if (qt_retorno_w = 0) then

			select 	count(*)
			into STRICT	qt_retorno_w
			from   	laudo_paciente a
			where  	a.nr_sequencia = nr_sequencia_p
			and	exists (SELECT 	1
					from	laudo_paciente_loc d
					where  	d.cd_setor_registro = cd_setor_registro_w
					and	d.nr_sequencia = a.nr_sequencia
					and 	(d.dt_retirada IS NOT NULL AND d.dt_retirada::text <> ''));

			qt_result_w := 3;

		end if;
	end if;
end if;

return	qt_result_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
