CREATE OR REPLACE FUNCTION obter_legenda_status_lote_opme (nr_seq_status_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w    varchar(255);


BEGIN

if (nr_seq_status_p IS NOT NULL AND nr_seq_status_p::text <> '') then

	SELECT 	MAX(DS_COR)
	into STRICT	ds_retorno_w
	FROM 	lote_producao_status
	WHERE	ie_situacao = 'A'
	AND	nr_sequencia = nr_seq_status_p;

end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
