CREATE OR REPLACE FUNCTION obter_legenda_status_tarefa (nr_seq_status_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w    varchar(15);


BEGIN

if (nr_seq_status_p IS NOT NULL AND nr_seq_status_p::text <> '') and (ie_opcao_p = 'A') then

	SELECT 	substr(max(ds_cor_fundo),1,15)
	into STRICT	ds_retorno_w
	FROM 	STATUS_TAREFA_TASY
	WHERE	ie_situacao = 'A'
	AND	nr_sequencia = nr_seq_status_p;

elsif (nr_seq_status_p IS NOT NULL AND nr_seq_status_p::text <> '') and (ie_opcao_p = 'B') then

	select	substr(max(ds_cor_fonte),1,15)
	into STRICT	ds_retorno_w
	from	STATUS_TAREFA_TASY
	WHERE	ie_situacao = 'A'
	AND	nr_sequencia = nr_seq_status_p;

end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
