SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_leito_servico ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

if (ie_opcao_p = 'CDU') then
	select	cd_unidade_basica || ' - ' || cd_unidade_compl
	into STRICT	ds_retorno_w
	from    unidade_atendimento a,
		sl_unid_atend b
	where   b.nr_seq_unidade = a.nr_seq_interno
	and	b.nr_sequencia = nr_sequencia_p;
elsif (ie_opcao_p = 'DSN') then
	select	substr(obter_nome_setor(cd_setor_atendimento), 1, 100) || ' - ' || cd_unidade_basica || ' - ' || cd_unidade_compl
	into STRICT	ds_retorno_w
	from    unidade_atendimento a,
		sl_unid_atend b
	where   b.nr_seq_unidade = a.nr_seq_interno
	and	b.nr_sequencia = nr_sequencia_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
