SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_leito_setor_ant (nr_atendimento_p bigint, ie_opcao text) RETURNS varchar AS $body$
DECLARE

    nm_return_w  varchar(200);
    cd_setor_atendimento_w bigint;
    cd_unidade_basica_w  varchar(200);
    cd_unidade_compl_2_w varchar(200);


BEGIN

/*
L = Nome do leito na integracao, caso nao tiver nome da unidade
C = Caso nao houver nome de leito da integracao, retorna o complemento
S = Nome do setor na integracao, caso nao tiver nome do setor
*/
if (ie_opcao = 'L') then     
    select	coalesce(max(c.nm_leito_integracao),max(c.CD_UNIDADE_BASICA))
    into STRICT nm_return_w
    from	atend_paciente_unidade a,
               unidade_atendimento c
    where	(a.dt_saida_unidade IS NOT NULL AND a.dt_saida_unidade::text <> '')
    and a.cd_setor_atendimento = c.cd_setor_atendimento
    and	a.cd_unidade_basica    = c.cd_unidade_basica
    and a.cd_unidade_compl 	   = c.cd_unidade_compl
    and	a.nr_atendimento       = nr_atendimento_p
    and	a.dt_entrada_unidade   = (SELECT 	max(b.dt_entrada_unidade)
                                  from	atend_paciente_unidade b
                                  where	b.nr_atendimento = nr_atendimento_p
								  AND   b.IE_PASSAGEM_SETOR = 'N'
                                  and	(b.dt_saida_unidade IS NOT NULL AND b.dt_saida_unidade::text <> ''));

elsif (ie_opcao = 'S') then 
    select	max(cc.cd_setor_atendimento),
            max(cc.cd_unidade_basica),
            max(cc.cd_unidade_compl)
    into STRICT             
        cd_setor_atendimento_w,
        cd_unidade_basica_w,
        cd_unidade_compl_2_w
    from atend_paciente_unidade aa,
         unidade_atendimento cc
    where	(aa.dt_saida_unidade IS NOT NULL AND aa.dt_saida_unidade::text <> '')
    and aa.cd_setor_atendimento = cc.cd_setor_atendimento
    and aa.cd_unidade_basica  	= cc.cd_unidade_basica
    and aa.cd_unidade_compl 	  = cc.cd_unidade_compl
    and aa.nr_atendimento       = nr_atendimento_p
    and aa.dt_entrada_unidade   = (SELECT max(bb.dt_entrada_unidade)
                                   from	atend_paciente_unidade bb
                                   where	bb.nr_atendimento = nr_atendimento_p
								   AND bb.IE_PASSAGEM_SETOR = 'N'
                                   and	(bb.dt_saida_unidade IS NOT NULL AND bb.dt_saida_unidade::text <> ''));
    
    select Obter_point_of_care(cd_setor_atendimento_w,
                               cd_unidade_basica_w,
                               cd_unidade_compl_2_w)
    into STRICT  nm_return_w      
;

elsif (ie_opcao = 'C') then 
	
	select	max(c.CD_UNIDADE_COMPL)
    into STRICT nm_return_w
    from	atend_paciente_unidade a,
               unidade_atendimento c
    where	(a.dt_saida_unidade IS NOT NULL AND a.dt_saida_unidade::text <> '')
    and a.cd_setor_atendimento = c.cd_setor_atendimento
    and	a.cd_unidade_basica    = c.cd_unidade_basica
    and a.cd_unidade_compl 	   = c.cd_unidade_compl
	and coalesce(c.nm_leito_integracao::text, '') = ''
    and	a.nr_atendimento       = nr_atendimento_p
    and	a.dt_entrada_unidade   = (SELECT 	max(b.dt_entrada_unidade)
                                  from	atend_paciente_unidade b
                                  where	b.nr_atendimento = nr_atendimento_p
								  AND b.IE_PASSAGEM_SETOR = 'N'
                                  and	(b.dt_saida_unidade IS NOT NULL AND b.dt_saida_unidade::text <> ''));
  
end if;

return nm_return_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
