CREATE OR REPLACE FUNCTION obter_liber_etapa_usuario ( nr_seq_etapa_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, cd_setor_atendimento_p bigint, cd_convenio_p bigint) RETURNS varchar AS $body$
DECLARE


ie_liberado_w		varchar(1);
qt_registros_w		bigint;
qt_regras_w		bigint;
cd_setor_atendimento_w	bigint;
cd_perfil_ativo_w		bigint;


BEGIN

ie_liberado_w:= 'S';

/* Verificar se existe alguma regra */

select	count(*)
into STRICT	qt_regras_w
from 	fatur_etapa		a,
	fatur_etapa_usuario	b
where 	a.nr_sequencia 		= b.nr_seq_etapa
and 	a.cd_estabelecimento 	= cd_estabelecimento_p
and 	b.nr_seq_etapa		= nr_seq_etapa_p;
--and	nvl(a.ie_situacao,'A') = 'A'; Retirado pois estava apresentando as inativas - Geliard 01/01/2011
if (qt_regras_w > 0) then

	ie_liberado_w			:= 'N';
	cd_setor_atendimento_w		:= coalesce(cd_setor_atendimento_p,0);
	cd_perfil_ativo_w			:= coalesce(obter_perfil_ativo,0);

	select	count(*)
	into STRICT	qt_registros_w
	from 	fatur_etapa a,
		fatur_etapa_usuario	b
	where 	a.nr_sequencia 					= b.nr_seq_etapa
	and 	a.cd_estabelecimento 				= cd_estabelecimento_p
	and 	b.nr_seq_etapa					= nr_seq_etapa_p
	and 	coalesce(b.nm_usuario_lib,nm_usuario_p) 			= nm_usuario_p
	--and	nvl(b.cd_setor_atendimento,cd_setor_atendimento_w)	= cd_setor_atendimento_w
	and	((obter_se_setor_usuario(coalesce(b.cd_setor_atendimento,cd_setor_atendimento_w),nm_usuario_p) = 'S') or (coalesce(b.cd_setor_atendimento::text, '') = ''))
	and	coalesce(b.cd_perfil,cd_perfil_ativo_w)			= cd_perfil_ativo_w
	and	((coalesce(b.cd_convenio, coalesce(cd_convenio_p,0)) = coalesce(cd_convenio_p,0)) or (coalesce(cd_convenio_p,0) = 0))
	and	coalesce(a.ie_situacao,'A')				= 'A';

	if (qt_registros_w > 0) then
		ie_liberado_w:= 'S';
	end if;

end if;

return	ie_liberado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
