CREATE OR REPLACE FUNCTION obter_lib_classif_transcr ( nr_seq_classif_p bigint, cd_perfil_p bigint, cd_setor_p bigint) RETURNS varchar AS $body$
DECLARE


cd_perfil_w	integer;
cd_setor_w	integer;
ie_regra_w	varchar(1);
ie_lib_w	varchar(1);
qt_regra_w	bigint;


BEGIN
if (nr_seq_classif_p IS NOT NULL AND nr_seq_classif_p::text <> '') then
	cd_perfil_w	:= coalesce(cd_perfil_p,0);
	cd_setor_w	:= coalesce(cd_setor_p,0);

	select	count(*)
	into STRICT	qt_regra_w
	from	classif_transcr_lib
	where	nr_seq_classificacao	= nr_seq_classif_p;

	if (qt_regra_w = 0) then
		ie_lib_w	:= 'S';
	else
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_lib_w
		from	classif_transcr_lib
		where	nr_seq_classificacao 			= nr_seq_classif_p
		and	coalesce(cd_perfil,cd_perfil_w)		= cd_perfil_w
		and	coalesce(cd_setor_atendimento,cd_setor_w)	= cd_setor_w;
	end if;
else
	ie_lib_w	:= 'S';
end if;

return ie_lib_w;

end;
$body$
