CREATE OR REPLACE FUNCTION obter_lib_erro_regra_rep (nr_seq_regra_p bigint, cd_perfil_p bigint) RETURNS varchar AS $body$
DECLARE


ie_forma_consistir_w	varchar(15);
ie_lib_erro_wheb_w		varchar(15);
ie_lib_erro_perfil_w		varchar(15);
ie_lib_erro_w		varchar(15);

c01 CURSOR FOR
SELECT	ie_forma_consistencia
from	regra_consiste_prescr_par
where	nr_seq_regra 		= nr_seq_regra_p
and	coalesce(cd_perfil,cd_perfil_p)	= cd_perfil_p
order by
	nr_sequencia;


BEGIN
if (nr_seq_regra_p IS NOT NULL AND nr_seq_regra_p::text <> '') then
	select	max(ie_forma_consistencia),
		max(ie_libera_prescr)
	into STRICT	ie_forma_consistir_w,
		ie_lib_erro_wheb_w
	from	regra_consiste_prescr
	where	nr_sequencia = nr_seq_regra_p;

	if (ie_forma_consistir_w = 'R') then
		open c01;
		loop
		fetch c01 into ie_lib_erro_perfil_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			begin
			ie_lib_erro_perfil_w	:= ie_lib_erro_perfil_w;
			end;
		end loop;
		close c01;
	end if;
	ie_lib_erro_w	:= coalesce(ie_lib_erro_perfil_w,ie_lib_erro_wheb_w);
end if;

return ie_lib_erro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
