CREATE OR REPLACE FUNCTION obter_lib_funcao_medic_espec ( cd_funcao_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

						 
ie_possui_w		varchar(1);
ie_retorno_w		varchar(1) := 'S';


BEGIN 
 
select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
into STRICT	ie_possui_w	 
from	funcao_medic_especialidade 
where	cd_funcao = cd_funcao_p;
 
if (ie_possui_w = 'S') then 
 
	SELECT coalesce(MAX('S'),'N') 
	into STRICT  ie_retorno_w	 
	from  funcao_medic_especialidade 
	where cd_funcao = cd_funcao_p 
	and  cd_especialidade in (SELECT cd_especialidade  
	 			  from  medico_especialidade 
				  where cd_pessoa_fisica = cd_pessoa_fisica_p);
end if;	
 
return	ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
