CREATE OR REPLACE FUNCTION obter_lib_gerar_nf_saida_trans ( nr_ordem_compra_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


qt_existe_w	bigint;
ie_libera_w	varchar(1);

ie_somente_barras_atend_w	varchar(1);
ie_tipo_itens_w	varchar(1);
cd_estabelecimento_w	smallint := wheb_usuario_pck.get_cd_estabelecimento;

BEGIN
ie_libera_w := 'N';
ie_tipo_itens_w := obter_valor_param_usuario(146,9,obter_perfil_ativo,nm_usuario_p,cd_estabelecimento_w);
ie_somente_barras_atend_w := obter_valor_param_usuario(146,27,obter_perfil_ativo,nm_usuario_p,cd_estabelecimento_w);

if (ie_somente_barras_atend_w = 'S') then
	begin
	select	count(*)
	into STRICT	qt_existe_w
	from	ordem_compra_item_cb
	where	nr_ordem_compra 	= nr_ordem_compra_p
	and	ie_atende_recebe	= 'A'
	and	coalesce(nr_seq_nota::text, '') = ''
	and	ie_status = 'CB';

	if (qt_existe_w > 0) then
		ie_libera_w := 'S';
	end if;
	end;
elsif (ie_tipo_itens_w = 'C') then
	begin
	select	count(*)
	into STRICT	qt_existe_w
	from	ordem_compra_item_cb
	where	nr_ordem_compra 	= nr_ordem_compra_p
	and	ie_atende_recebe	= 'A'
	and	coalesce(nr_seq_nota::text, '') = '';

	if (qt_existe_w > 0) then
		ie_libera_w := 'S';
	end if;
	end;
else
	ie_libera_w := 'S';
end if;

return	ie_libera_w;
end;
$body$
