SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_lib_mat_padrao ( cd_material_p bigint, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_liberado_w			varchar(2);
cd_pessoa_fisica_w		bigint;
nr_sequencia_w			bigint;
dt_liberacao_w			timestamp;
dt_suspenso_w			timestamp;
dt_inicio_validade_w		timestamp;


BEGIN 
cd_pessoa_fisica_w	:= obter_pessoa_atendimento(nr_atendimento_p,'C');
 
select	max(coalesce(nr_sequencia,0)) 
into STRICT	nr_sequencia_w 
from	lib_material_paciente 
where	cd_pessoa_fisica = cd_pessoa_fisica_w 
and	coalesce(DT_SUSPENSO::text, '') = '' 
and	cd_material = cd_material_p;
 
select	max(dt_liberacao), 
	max(dt_suspenso), 
	max(dt_inicio_validade) 
into STRICT	dt_liberacao_w, 
	dt_suspenso_w, 
	dt_inicio_validade_w 
from	lib_material_paciente 
where	nr_sequencia = nr_sequencia_w;
 
if (dt_liberacao_w IS NOT NULL AND dt_liberacao_w::text <> '') and (coalesce(dt_suspenso_w::text, '') = '') and (dt_inicio_validade_w < clock_timestamp()) then 
 
	ie_liberado_w	:= 'L';
 
elsif (coalesce(dt_suspenso_w::text, '') = '') and (dt_inicio_validade_w > clock_timestamp()) then 
 
	ie_liberado_w	:= 'FV';
 
elsif (dt_suspenso_w IS NOT NULL AND dt_suspenso_w::text <> '') then 
 
	ie_liberado_w	:= 'S';
 
elsif (coalesce(dt_liberacao_w::text, '') = '') and (coalesce(dt_suspenso_w::text, '') = '') and (dt_inicio_validade_w < clock_timestamp()) then 
	 
	ie_liberado_w	:= 'N';
	 
end if;
	 
	 
	 
return	ie_liberado_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
