CREATE OR REPLACE FUNCTION obter_lib_tipo_guia (cd_estabelecimento_p bigint, ie_tipo_guia_p text, ie_clinica_p bigint) RETURNS varchar AS $body$
DECLARE


ie_liberado_w			varchar(01);
qt_reg_w			bigint;


BEGIN

select	count(*)
into STRICT	qt_reg_w
from 	TIPO_GUIA_CLINICA
where 	ie_clinica = ie_clinica_p;

If (qt_reg_w = 0) then
	ie_liberado_w := 'S';

elsif (qt_reg_w > 0) then

	select	coalesce(max('S'), 'N')
	into STRICT	ie_liberado_w
	from	TIPO_GUIA_CLINICA
	where	ie_clinica					= ie_clinica_p
	and	ie_tipo_guia					= ie_tipo_guia_p
	and	coalesce(cd_estabelecimento, cd_estabelecimento_p)	= cd_estabelecimento_p;
end if;

return	ie_liberado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
