CREATE OR REPLACE FUNCTION obter_licenca_cliente (nr_seq_cliente_p bigint) RETURNS varchar AS $body$
DECLARE


dt_licenca_w		timestamp;
ds_retorno_w		varchar(255) := '';
nr_seq_tipo_contrato_w	bigint;
ds_tipo_contrato_w	varchar(255) := '';
ds_abreviacao_w		tipo_contrato.ds_abreviacao%type;

c01 CURSOR FOR
SELECT	distinct a.dt_inicio,
	a.nr_seq_tipo_contrato,
	b.ds_abreviacao
from	contrato a,
	tipo_contrato b
where	a.nr_seq_cliente = nr_seq_cliente_p
and	a.ie_situacao = 'A'
and	a.ie_classificacao in ('AT','OR')
and	a.nr_seq_tipo_contrato = b.nr_sequencia
and	coalesce(b.ie_gestao_financeira, 'N') = 'S';


BEGIN
open C01;
loop
fetch C01 into
	dt_licenca_w,
	nr_seq_tipo_contrato_w,
	ds_abreviacao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (ds_abreviacao_w IS NOT NULL AND ds_abreviacao_w::text <> '') then
		ds_tipo_contrato_w := ds_abreviacao_w;
	end if;

	ds_retorno_w := ds_retorno_w || ' ';
	ds_retorno_w := ds_retorno_w || '('||to_char(dt_licenca_w,'dd/mm/yyyy')||')';
	if (ds_tipo_contrato_w IS NOT NULL AND ds_tipo_contrato_w::text <> '') then
		ds_retorno_w := ds_retorno_w || ' - ' || ds_tipo_contrato_w;
	end if;

	end;
end loop;
close C01;

ds_retorno_w := trim(both ds_retorno_w);

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
