CREATE OR REPLACE FUNCTION obter_limite_contato_atingido (NR_SEQ_LISTA_ESPERA_P bigint) RETURNS varchar AS $body$
DECLARE

  QT_CONTATOS_W    bigint;
  QT_LIMITE_CONTATO_W bigint;

BEGIN

  SELECT OBTER_QTD_CONTATOS_PENDENTES(NR_SEQ_LISTA_ESPERA_P)
  INTO STRICT QT_CONTATOS_W
;

  SELECT MAX(B.QTD_LIMITE_CONTATOS)
  INTO STRICT QT_LIMITE_CONTATO_W
  FROM REGRA_LISTA_ESPERA_CONTATO B LIMIT 1;

  IF (QT_CONTATOS_W < QT_LIMITE_CONTATO_W ) THEN
    RETURN 'N';
  END IF;
  RETURN 'S';

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
