SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_linhas_pintar_ctb (nr_seq_regra_p bigint, nr_lote_contabil_p bigint, cd_empresa_p bigint, cd_estabelecimento_p bigint, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


nr_seq_mes_ref_w		bigint;

-- Valores do lote contabil
cd_conta_contabil_lc_w  varchar(20);
ds_conta_contabil_lc_w	varchar(255);
vl_credito_lc_w			double precision;
vl_debito_lc_w			double precision;



-- Valores do balancete
cd_conta_contabil_b_w  varchar(20);
ds_conta_contabil_b_w	varchar(255);
vl_credito_b_w			double precision;
vl_debito_b_w			double precision;


ds_retorno_w	varchar(4000);



--movimentos agrupado pelo valor de debito de credito de um determinado lote
c01 CURSOR FOR
SELECT	cd_conta_contabil,
	ds_conta_contabil,
	SUM((REPLACE(vl_debito, '.', ','))::numeric ) vl_debito,
	SUM((REPLACE(vl_credito, '.', ','))::numeric ) vl_credito
FROM	w_ctb_contab_mexico
WHERE	nr_seq_regra     	= nr_seq_regra_p
AND		obter_empresa_estab(cd_estabelecimento) = cd_empresa_p
AND 	cd_estabelecimento	= cd_estabelecimento_p
AND	nr_lote_contabil   = nr_lote_contabil_p
AND	(cd_conta_contabil IS NOT NULL AND cd_conta_contabil::text <> '')
GROUP BY cd_conta_contabil,
	ds_conta_contabil;

--movimentos do balancete agrupado pelo valor de debito e de credito
c02 CURSOR FOR
SELECT	cd_conta_contabil,
	ds_conta_apres ds_conta_contabil,
	SUM(vl_debito) vl_debito,
	SUM(vl_credito) vl_credito
FROM	ctb_balancete_v
where	nr_seq_mes_ref = nr_seq_mes_ref_w
AND		cd_empresa 		 	= cd_empresa_p
and    ie_normal_encerramento = 'N'
AND 	cd_estabelecimento	= cd_estabelecimento_p
GROUP BY cd_conta_contabil, ds_conta_apres;




BEGIN

select	max(nr_sequencia )
into STRICT	nr_seq_mes_ref_w
from	ctb_mes_ref
where	dt_referencia = dt_referencia_p;
--passar pelo java para a procedure a data pronta (concatenar dia 01/mes que esta na grid/ano que esta na grid)
--talvez seja necessario utilizar o to_date
--abrir cursor01
open c01;
loop
fetch c01 into
	cd_conta_contabil_lc_w,
	ds_conta_contabil_lc_w,
	vl_debito_lc_w,
	vl_credito_lc_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
begin
	open c02;
	loop
	fetch c02 into
		cd_conta_contabil_b_w,
		ds_conta_contabil_b_w,
		vl_debito_b_w,
		vl_credito_b_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
	begin
		if (cd_conta_contabil_lc_w = cd_conta_contabil_b_w) then
			if (vl_credito_lc_w = vl_credito_b_w AND vl_debito_lc_w = vl_debito_b_w) then
				ds_retorno_w := ds_retorno_w || cd_conta_contabil_lc_w || '=1;';
			else
				ds_retorno_w := ds_retorno_w || cd_conta_contabil_lc_w || '=0;';
			end if;
		end if;
	end;
	end loop;
	close c02;
end;
end loop;
close c01;
return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_linhas_pintar_ctb (nr_seq_regra_p bigint, nr_lote_contabil_p bigint, cd_empresa_p bigint, cd_estabelecimento_p bigint, dt_referencia_p timestamp) FROM PUBLIC;

